// 封装一个函数，实现倒序字符串功能
function strReverseFun(str) {
	var arr = [];
	for (var i = 0; i < str.length; i++) {
		arr.push(str.charAt(i));
	}
	arr = arr.reverse().join("");
	return arr;
}


/* 封装一个截取对应结果的函数
	var str = "abcdefg";
	console.log(mySlice(str, 0, 3)); //abcd
	console.log(mySlice(str, 2, 3)); //cd
*/
function mySlice(str, origin, target) {
	return str.slice(origin, target + 1);
}



//封装一个计算两个整数的差值的函数
// var num1 = 10;
// var num2 = 20;
function diff(a, b) {
	if (a >= b) {
		document.write(a + "和" + b + "的差值是：" + (a - b))
	} else {
		document.write(a + "和" + b + "的差值是：" + (b - a))
	}
}
