// 封装一个函数判断是否存在某个字符串返回布尔值
function exists(string, character) {
	return string.indexOf(character) != -1;
}



// 用于倒计时 显示目标日到今日的时间差
function showDateDiff() {
	var sdDate = new Date("2020,12,25");
	var currentDate = new Date();
	//圣诞的那天的毫秒数
	var sdTime = sdDate.getTime();
	// 当前时间的毫秒数
	var curtTime = currentDate.getTime();
	// 圣诞与当前时间的毫秒数的差值
	var timeDiff = sdTime - curtTime

	//换算的时间  秒
	var seconds = dateFormat(parseInt(timeDiff / 1000 % 60));
	// 分
	var minutes = dateFormat(parseInt(timeDiff / 1000 / 60 % 60));
	// 时
	var hours = dateFormat(parseInt(timeDiff / 1000 / 60 / 60 % 24));
	// 天
	var date = dateFormat(parseInt(timeDiff / 1000 / 60 / 60 / 24));

	// var div = document.getElementsByTagName("div")[0];
	// div.innerHTML = ("圣诞倒计时：" + date + "天" + hours + "时" + minutes + "分" + seconds + "秒")
}
