// 规定时间格式
function dateFormat(item) {
	if (item < 10) {
		item = "0" + item;
	}
	return item
}



// 获取元素样式
function getStyleFn(obj, attr) {
	if (obj.currentStyle) {
		alert(obj.currentStyle[attr])	//由于传过来的属性是字符串，所以用[]代替 " . "

	} else {
		alert(document.defaultView.getComputedStyle(obj, null)[attr])
	}

}




// 手风琴构造函数
// var gallery = new XyGallery("gallery", { width: 4, height: 5 })
function XyGallery(idBox, options, speed) {
	//获取最大的盒子
	var container = document.getElementById(idBox);

	//获取内容描述盒子
	var contentBox = container.getElementsByClassName("contentBox");
	// 给idBox 元素动态添加类
	if (container.className.indexOf("gallery") == -1) container.className += "gallery"


	// 判断用户是否输入width
	if (options.width == undefined) throw "缺少width"
	//判断用户是否输入height
	if (options.height == undefined) throw "缺少height"
	// 判断用户输入的width*height是否等于手风琴方块的个数
	if (options.width * options.height != container.children.length) {
		throw "输入的width & height 的积不等于" + idBox + "子元素的个数"
	}

	//定义大盒子和小盒子的固定宽高
	var defaultPicture = {
		activeWidth: 340, // 最大
		activeHeight: 400,// 最大
		defaultWidth: 130,// 最小
		defaultHeight: 40 // 最小
	}
	//将用户传入的对象与defaultPicture对象合并，方便之后的操作
	options = Object.assign({}, defaultPicture, options);
	//设置 container 元素的总宽度
	container.style.width = (options.width - 1) * options.defaultWidth + options.activeWidth + "px"
	// 设置container元素里的内容盒子宽度
	for (var i = 0; i < contentBox.length; i++) {
		contentBox[i].style.width = options.activeWidth + "px";
	}


	//记录旧时间
	var oldTime = new Date(0);
	var setTimeoutId = null; // 初始化定时器

	// 给所有div添加移入事件
	for (var i = 0; i < container.children.length; i++) {
		container.children[i].index = i;    //将 i 保存在对应的元素属性里，方便之后读取
		container.children[i].addEventListener("mouseenter", function () {
			activePicture(this.index);
		})
	}




	// 手风琴动画
	function activePicture(index) {
		clearTimeout(setTimeoutId); //清除定时器
		// 获取当前时间
		var currentTime = new Date();
		if (currentTime - oldTime < speed) { //如果执行此函数时，当前的时间与上一次执行函数的时间差小于 300
			setTimeoutId = setTimeout(function () {
				activePicture(index);
			}, speed)
			return
		}
		// 把旧时间跟新为当前时间
		oldTime = currentTime;


		// 更具当前索引找到当前元素的坐标
		var cx = index % options.width
		var cy = Math.floor(index / options.width)

		// 遍历行，行执行一次，列执行一遍
		for (var iy = 0; iy < options.height; iy++) {
			// 遍历列
			for (var ix = 0; ix < options.width; ix++) {
				// 根据坐标求出所有元素的索引位 
				var cindex = options.width * iy + ix;
				var itemBox = container.children[cindex]; // 当前遍历到的元素
				if (cx == ix && cy == iy) { //表示此时的盒子是 索引位index的盒子，同一个盒子
					itemBox.style.width = options.activeWidth + 'px';
					itemBox.style.height = options.activeHeight + 'px'
				} else if (cx == ix) { // 表示与大盒子在同一列的盒子
					itemBox.style.width = options.activeWidth + "px"
					itemBox.style.height = options.defaultHeight + "px"
				} else if (cy == iy) {  // 表示与大盒子在同一行的盒子
					itemBox.style.width = options.defaultWidth + "px"
					itemBox.style.height = options.activeHeight + "px"
				} else { //既不在同一排也不在同一列的盒子
					itemBox.style.width = options.defaultWidth + "px"
					itemBox.style.height = options.defaultHeight + "px"
				}

			}

		}


	}
	activePicture(0)

	return {
		activePicture: activePicture
	}
}









